// contractConfig.js
import axios from 'axios';

// 默认合约地址（将在运行时被替换）
const defaultAddresses = {
  ContractAddress: "",
  VotingContractAddress: "",
  classAddress: "",
  teachervoteAddress: ""
};

// 创建一个异步函数来获取合约地址
export const getContractAddresses = async () => {
  try {
    const response = await axios.get('/api/contract-addresses');
    if (response.data.code === 0) {
      return response.data.data;
    }
    return defaultAddresses;
  } catch (error) {
    console.error('获取合约地址失败:', error);
    return defaultAddresses;
  }
};

// 导出默认地址作为兼容性措施
export default defaultAddresses;
